package chapter02;

public class Bit_Test02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int num = 0B00000101; // 5
		
		System.out.println(num << 2); //값을 증가시킨다
		
		System.out.println(num); //참고로, 연산은 리턴이 아니다
		
		System.out.println(num >>2); //값을 감소
		
		
		int a= 5, b= 2, c= a>>b;
		
		
		
		
		
		
	}

}
